import React, { useState, useEffect, useRef } from "react";
import "./App.css";

const App = () => {
  const [loading, setLoading] = useState(true);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState("");
  const [showTypingIndicator, setShowTypingIndicator] = useState(false);
  const [error, setError] = useState("");
  const messagesEndRef = useRef(null);

  // Simulate loading
  useEffect(() => {
    const timer = setTimeout(() => {
      setLoading(false);
    }, 3000);

    return () => clearTimeout(timer);
  }, []);

  // Scroll to bottom of messages
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [messages]);

  // Function to format text with bold formatting for content between asterisks
  const formatTextWithBold = (text) => {
    if (!text) return "";

    // Replace asterisks with bold tags
    const formattedText = text.replace(/\*(.*?)\*/g, '<strong class="highlighted-text">$1</strong>');

    // Replace newlines with line breaks
    return formattedText.replace(/\n/g, "<br />");
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();

    if (inputMessage.trim() === "") return;

    // Add user message
    const newUserMessage = {
      text: inputMessage,
      sender: "user",
      timestamp: new Date().toLocaleTimeString([], {
        hour: "2-digit",
        minute: "2-digit",
      }),
    };

    setMessages([...messages, newUserMessage]);
    setInputMessage("");
    setShowTypingIndicator(true);
    setError("");

    // Send request to backend
    try {
      const response = await fetch("http://localhost:8000/api/chat/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ question: inputMessage }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Something went wrong");
      }

      const data = await response.json();

      // Add bot response
      const newBotMessage = {
        text:
          data.answer ||
          "I apologize, but I could not generate a response at this time.",
        sender: "bot",
        timestamp: new Date().toLocaleTimeString([], {
          hour: "2-digit",
          minute: "2-digit",
        }),
      };

      setShowTypingIndicator(false);
      setMessages((prevMessages) => [...prevMessages, newBotMessage]);
    } catch (err) {
      setShowTypingIndicator(false);
      setError(err.message || "Failed to fetch response.");

      // Add error message
      const errorMessage = {
        text: `Error: ${
          err.message || "Failed to connect to AyurGPT. Please try again."
        }`,
        sender: "bot",
        isError: true,
        timestamp: new Date().toLocaleTimeString([], {
          hour: "2-digit",
          minute: "2-digit",
        }),
      };

      setMessages((prevMessages) => [...prevMessages, errorMessage]);
    }
  };

  // Function to handle suggestion chip clicks
  const handleSuggestionClick = (suggestion) => {
    setInputMessage(suggestion);
  };

  // Add this code at the bottom of your App.js file before the export

// At the end of your useEffect that runs once on load, add this code
useEffect(() => {
  // Mobile sidebar toggle functionality
  const mobileMenuButton = document.querySelector('.mobile-menu-button');
  const sidebar = document.querySelector('.sidebar');
  const body = document.body;

  if (mobileMenuButton) {
    mobileMenuButton.addEventListener('click', () => {
      sidebar.classList.toggle('show');
      body.classList.toggle('sidebar-open');
    });

    // Close sidebar when clicking outside
    document.addEventListener('click', (e) => {
      if (body.classList.contains('sidebar-open') && 
          !sidebar.contains(e.target) && 
          e.target !== mobileMenuButton) {
        sidebar.classList.remove('show');
        body.classList.remove('sidebar-open');
      }
    });
  }

  return () => {
    if (mobileMenuButton) {
      mobileMenuButton.removeEventListener('click', () => {
        sidebar.classList.toggle('show');
        body.classList.toggle('sidebar-open');
      });
    }
  };
}, []);
  return (
    <div className="app-container">
      {loading ? (
        <div className="loading-screen">
          <div className="leaf-container">
            <svg
              className="ayurvedic-leaf"
              viewBox="0 0 100 100"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                d="M50,10 C70,10 90,30 90,50 C90,70 70,90 50,90 C30,90 10,70 10,50 C10,30 30,10 50,10 Z"
                fill="none"
                stroke="#4CAF50"
                strokeWidth="1"
              />
              <path
                className="leaf-vein"
                d="M50,10 C50,30 50,70 50,90"
                stroke="#388E3C"
                strokeWidth="1"
              />
              <path
                className="leaf-vein"
                d="M30,30 C40,40 60,60 70,70"
                stroke="#388E3C"
                strokeWidth="1"
              />
              <path
                className="leaf-vein"
                d="M70,30 C60,40 40,60 30,70"
                stroke="#388E3C"
                strokeWidth="1"
              />
              <path
                className="leaf-fill"
                d="M50,10 C70,10 90,30 90,50 C90,70 70,90 50,90 C30,90 10,70 10,50 C10,30 30,10 50,10 Z"
                fill="#4CAF50"
                fillOpacity="0.3"
              />
            </svg>
            <div className="loading-text">Balancing your doshas...</div>
          </div>
        </div>
      ) : (
        <div className="chat-container">
          <div className="sidebar">
            <div className="sidebar-header">
              <div className="logo">
                <svg
                  className="small-leaf"
                  viewBox="0 0 100 100"
                  xmlns="http://www.w3.org/2000/svg"
                  width="24"
                  height="24"
                >
                  <path
                    d="M50,10 C70,10 90,30 90,50 C90,70 70,90 50,90 C30,90 10,70 10,50 C10,30 30,10 50,10 Z"
                    fill="#4CAF50"
                  />
                </svg>
                <h1>
                  <a
                    href="/"
                    style={{ textDecoration: "none", color: "inherit" }}
                  >
                    AyurGPT
                  </a>
                </h1>
              </div>
            </div>
            <div className="sidebar-content">
              <div className="sidebar-section">
                <h3>Explore Ayurveda</h3>
                <ul className="sidebar-nav">
                  <li>
                    <button onClick={() => handleSuggestionClick("What are the three doshas in Ayurveda?")}>
                      The Three Doshas
                    </button>
                  </li>
                  <li>
                    <button onClick={() => handleSuggestionClick("What is Prakriti in Ayurveda?")}>
                      Understanding Prakriti
                    </button>
                  </li>
                  <li>
                    <button onClick={() => handleSuggestionClick("What are the principles of Ayurvedic diet?")}>
                      Ayurvedic Diet
                    </button>
                  </li>
                  <li>
                    <button onClick={() => handleSuggestionClick("What are common Ayurvedic herbs and their benefits?")}>
                      Medicinal Herbs
                    </button>
                  </li>
                  <li>
                    <button onClick={() => handleSuggestionClick("What is dinacharya in Ayurveda?")}>
                      Daily Routine (Dinacharya)
                    </button>
                  </li>
                </ul>
              </div>
            </div>
            <div className="sidebar-footer">
              <button className="theme-toggle">
                <span className="sr-only">Toggle theme</span>
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="18"
                  height="18"
                  viewBox="0 0 24 24"
                  fill="none"
                  stroke="currentColor"
                  strokeWidth="2"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                >
                  <path d="M12 16a4 4 0 1 0 0-8 4 4 0 0 0 0 8z"></path>
                  <path d="M12 8V4"></path>
                  <path d="M12 20v-4"></path>
                  <path d="M20 12h-4"></path>
                  <path d="M4 12h4"></path>
                </svg>
                <span>Theme</span>
              </button>
            </div>
          </div>

          <div className="main-content">
            <header className="chat-header">
              <button className="mobile-menu-button">
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="24"
                  height="24"
                  viewBox="0 0 24 24"
                  fill="none"
                  stroke="currentColor"
                  strokeWidth="2"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                >
                  <line x1="3" y1="12" x2="21" y2="12"></line>
                  <line x1="3" y1="6" x2="21" y2="6"></line>
                  <line x1="3" y1="18" x2="21" y2="18"></line>
                </svg>
              </button>
              <div className="header-title">
                <h2>Chat with AyurGPT</h2>
              </div>
              <div className="header-buttons">
                <button className="theme-toggle mobile-theme-toggle">
                  <span className="sr-only">Toggle theme</span>
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="18"
                    height="18"
                    viewBox="0 0 24 24"
                    fill="none"
                    stroke="currentColor"
                    strokeWidth="2"
                    strokeLinecap="round"
                    strokeLinejoin="round"
                  >
                    <path d="M12 16a4 4 0 1 0 0-8 4 4 0 0 0 0 8z"></path>
                    <path d="M12 8V4"></path>
                    <path d="M12 20v-4"></path>
                    <path d="M20 12h-4"></path>
                    <path d="M4 12h4"></path>
                  </svg>
                </button>
              </div>
            </header>

            <div className="messages-container">
              {messages.length === 0 ? (
                <div className="welcome-message">
                  <div className="welcome-leaf">
                    <svg
                      className="ayurvedic-leaf"
                      viewBox="0 0 100 100"
                      xmlns="http://www.w3.org/2000/svg"
                      width="60"
                      height="60"
                    >
                      <path
                        d="M50,10 C70,10 90,30 90,50 C90,70 70,90 50,90 C30,90 10,70 10,50 C10,30 30,10 50,10 Z"
                        fill="#4CAF50"
                        fillOpacity="0.8"
                      />
                      <path
                        d="M50,10 C50,30 50,70 50,90"
                        stroke="#fff"
                        strokeWidth="1"
                      />
                      <path
                        d="M30,30 C40,40 60,60 70,70"
                        stroke="#fff"
                        strokeWidth="1"
                      />
                      <path
                        d="M70,30 C60,40 40,60 30,70"
                        stroke="#fff"
                        strokeWidth="1"
                      />
                    </svg>
                  </div>
                  <h2>Welcome to AyurGPT</h2>
                  <p>Ask questions about Ayurvedic medicine and principles</p>
                  <div className="suggestion-chips">
                    <button
                      className="suggestion-chip"
                      onClick={() =>
                        handleSuggestionClick(
                          "What are the three doshas in Ayurveda?"
                        )
                      }
                    >
                      What are the three doshas?
                    </button>
                    <button
                      className="suggestion-chip"
                      onClick={() =>
                        handleSuggestionClick(
                          "Recommend herbs for improving digestion"
                        )
                      }
                    >
                      Herbs for digestion
                    </button>
                    <button
                      className="suggestion-chip"
                      onClick={() =>
                        handleSuggestionClick(
                          "What is the ideal daily routine according to Ayurveda?"
                        )
                      }
                    >
                      Daily Ayurvedic routine
                    </button>
                  </div>
                </div>
              ) : (
                <>
                  {messages.map((message, index) => (
                    <div
                      key={index}
                      className={`message ${message.sender} ${
                        message.isError ? "error" : ""
                      }`}
                    >
                      {message.sender === "bot" && (
                        <div className="avatar">
                          <svg
                            className="small-leaf"
                            viewBox="0 0 100 100"
                            xmlns="http://www.w3.org/2000/svg"
                            width="24"
                            height="24"
                          >
                            <path
                              d="M50,10 C70,10 90,30 90,50 C90,70 70,90 50,90 C30,90 10,70 10,50 C10,30 30,10 50,10 Z"
                              fill={message.isError ? "#f44336" : "#4CAF50"}
                            />
                          </svg>
                        </div>
                      )}
                      <div className="message-content">
                        <div
                          className="message-text"
                          dangerouslySetInnerHTML={{
                            __html: formatTextWithBold(message.text),
                          }}
                        ></div>
                        <div className="message-time">{message.timestamp}</div>
                      </div>
                    </div>
                  ))}
                  {showTypingIndicator && (
                    <div className="message bot typing">
                      <div className="avatar">
                        <svg
                          className="small-leaf"
                          viewBox="0 0 100 100"
                          xmlns="http://www.w3.org/2000/svg"
                          width="24"
                          height="24"
                        >
                          <path
                            d="M50,10 C70,10 90,30 90,50 C90,70 70,90 50,90 C30,90 10,70 10,50 C10,30 30,10 50,10 Z"
                            fill="#4CAF50"
                          />
                        </svg>
                      </div>
                      <div className="typing-indicator">
                        <span></span>
                        <span></span>
                        <span></span>
                      </div>
                    </div>
                  )}
                  <div ref={messagesEndRef} />
                </>
              )}
            </div>

            <form className="input-area" onSubmit={handleSendMessage}>
              <input
                type="text"
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                placeholder="Ask about Ayurvedic medicine..."
                className="message-input"
              />
              <button
                type="submit"
                className="send-button"
                disabled={inputMessage.trim() === "" || showTypingIndicator}
              >
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="20"
                  height="20"
                  viewBox="0 0 24 24"
                  fill="none"
                  stroke="currentColor"
                  strokeWidth="2"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                >
                  <line x1="22" y1="2" x2="11" y2="13"></line>
                  <polygon points="22 2 15 22 11 13 2 9 22 2"></polygon>
                </svg>
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
